<?phpinclude 'inc/header.php';?><html>    <body>        <p style="text-align: center;">            CloudFlare Resolver!<br>            Enter URL<br>            Credits for the script go to <a href="http://www.hackforums.net/showthread.php?tid=2660401">Phizo / Dan</a>        </p>        <br>        <p style="text-align: center;"><?phpif (isset($_POST['submit'])) {    $url = $_POST['url'];        function is_ipv4($ip)    {        return filter_var($ip, FILTER_VALIDATE_IP, FILTER_FLAG_IPV4) ? $ip : '(Null)';    }                if (!preg_match('/^(https?):\/\/(w{3}|w3)\./i', $url)) {        $url = "http://www." . $url;    }    $headers = get_headers($url, 1);    $server  = $headers['Server'];        $subs  = array(        'cpanel.',        'ftp.',        'mail.',        'webmail.',        'direct.',        'direct-connect.',        'record.',        'ssl.',        'dns.',        'help.',        'blog.',        'forum.'    );    $count = count($subs);        if (preg_match('/^(https?):\/\/(w{3}|w3)\./i', $url, $matches)) {        if ($matches[2] != 'www') {            $url = preg_replace('/^(https?):\/\//', '', $url);        } else {            $url = explode($matches[0], $url);            $url = $url[1];        }    }        if (is_array($server))        $server = $server[0];        if (preg_match('/cloudflare/i', $server))        echo "<br>[+] CloudFlare detected: {$server}<br>";    else        echo "<br>[+] CloudFlare wasn't detected, proceeding anyway.<br>";        echo '[+] IP: ' . is_ipv4(gethostbyname($url)) . "<br><br>";    echo "[+] Searching for more IP addresses.<br><br>";        for ($x = 0; $x < $count; $x++) {        $site = $subs[$x] . $url;        $ip   = is_ipv4(gethostbyname($site));                echo "Trying {$site}: {$ip}<br>";    }    echo "<br>[+] Finished.<br>";}?>    </p>        <div align="center">            <form method="post">                <input type="hidden" id="submit" name="submit" value="1">                 <input type="text" id="url" name="url" placeholder="CloudFlare">                                <input type="submit" >            </form>        </div>    </body></html>